import React, { useEffect, useState } from "react";

function App() {
  const [breakLength, setBreakLength] = useState(5);
  const [sessionLength, setSessionLength] = useState(25);
  const [minutes, setMinutes] = useState(25);
  const [seconds, setSeconds] = useState(59);
  const [clockState, setClockState] = useState("");

  const timer = () => {};

  // useEffect(() => {
  //   timer();
  // }, [clockState]);

  return (
    <div
      style={{ backgroundColor: "#1e555c", color: "white", height: "100vh" }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          height: "100vh",
        }}
      >
        <h1>25 + 5 Clock</h1>
        <div className="d-flex">
          <div className="mx-5">
            <h2>Break Length</h2>
            <div className="d-flex">
              <button
                style={{
                  backgroundColor: "inherit",
                  border: "none",
                  color: "white",
                  fontSize: "1.4rem",
                  fontWeight: "bolder",
                }}
                className="p-2 mx-4"
                onClick={() => {
                  const increase = breakLength + 1;
                  setBreakLength(increase);
                  console.log(increase);
                }}
              >
                ↑
              </button>
              <h2>{breakLength}</h2>
              <button
                style={{
                  backgroundColor: "inherit",
                  border: "none",
                  color: "white",
                  fontSize: "1.4rem",
                  fontWeight: "bolder",
                }}
                className="p-2 mx-4"
                onClick={() => {
                  if (breakLength > 1) {
                    const decrease = breakLength - 1;

                    setBreakLength(decrease);
                    console.log(decrease);
                  }
                }}
              >
                ↓
              </button>
            </div>
          </div>
          <div>
            <h2>Session Length</h2>
            <div className="d-flex">
              <button
                style={{
                  backgroundColor: "inherit",
                  border: "none",
                  color: "white",
                  fontSize: "1.4rem",
                  fontWeight: "bolder",
                }}
                className="p-2 mx-4"
                onClick={() => {
                  const increase = sessionLength + 1;
                  setSessionLength(increase);
                  setMinutes(increase);
                  console.log(increase);
                }}
              >
                ↑
              </button>
              <h2>{sessionLength}</h2>
              <button
                style={{
                  backgroundColor: "inherit",
                  border: "none",
                  color: "white",
                  fontSize: "1.4rem",
                  fontWeight: "bolder",
                }}
                className="p-2 mx-4"
                onClick={() => {
                  if (sessionLength > 1) {
                    const decrease = sessionLength - 1;
                    setMinutes(decrease);
                    setSessionLength(decrease);
                    console.log(decrease);
                  }
                }}
              >
                ↓
              </button>
            </div>
          </div>
        </div>
        <div
          className="m-3 px-5 py-2"
          style={{
            border: "1px solid #13353a",
            borderRadius: "10px",
            borderWidth: "10px",
          }}
        >
          <h3 style={{ textAlign: "center" }}>Session</h3>
          <p className="" style={{ fontSize: "3.5rem" }}>
            {minutes} : {seconds}
          </p>
        </div>
        <div>
          <i
            className="fa-sharp fa-solid fa-pause mx-3"
            onClick={() => {
              //setClockState("play");
              timer();
            }}
          ></i>
          <i
            className="fa-solid fa-arrows-rotate mx-3"
            onClick={() => {
              setMinutes(25);
              setSeconds(0);
              setBreakLength(5);
              setSessionLength(25);
            }}
          ></i>
        </div>
      </div>
    </div>
  );
}

export default App;
